import axios from 'axios';
import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/auth.context';


function HomePage(){
    const { user } = useContext(AuthContext);

    const navigate = useNavigate();

    const [state, setState] = useState({
        title: '',
        author: '',
        genre: '',
        category: ''
    });

    const updateState = event => setState({
        ...state,
        [event.target.name]: event.target.value
      });

    const handleSubmit = event => {
        
        
    };
    return(
        <div>
            <h1>Book Worm</h1>
            <h2>Add Book</h2>
            <input  
                type="text"
                placeholder="Title"
                value={state.title}
                onChange={updateState}
                required
            />
            <input
                type="text"
                placeholder="Author"
                value={state.author}
                onChange={updateState}
            />
            <input
                type="text"
                placeholder="Genre"
                name={state.genre}
                onChange={updateState}
            />
            <select name={state.category} required onChange={(e) => updateState}>
                <option value="">Select Category</option>
                <option value="planning">Planning to Read</option>
                <option value="reading">Currently Reading</option>
                <option value="finished">Finished Reading</option>
                </select>
                <button onClick={handleSubmit}>Add Book</button>
                <div>
                <h2>Categories</h2>
                <button>Planning to Read</button>
                <button>Currently Reading</button>
                <button>Finished Reading</button>
            </div>
        </div>
            
    )
}

export default HomePage;
