import { useEffect, useState } from 'react';

import axios from 'axios'

function BookList({view}){

    const [books, setBooks] = useState([]);

    const getBooks = () =>{
        const storedToken = localStorage.getItem('authToken');

        axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/books/${view}`,{
            headers: {
                Authorization: `Bearer ${storedToken}`
            }
        })
            .then(res =>{
                setBooks(res.data.books);
            })
            .catch(err => console.log(err))
    }

    useEffect(()=>{
        getBooks();
    },[view])

    const editBook = event =>{
        event.preventDefault();
        
        const storedToken = localStorage.getItem('authToken');

        let bookId = event._id;
        console.log(`BOOK ID: ${bookId}`);

        axios.put(`${process.env.REACT_APP_BACKEND_URL}/api/books/${bookId}/edit-category`,{
            headers: {
                Authorization: `Bearer ${storedToken}`
            },
            body: {
                category: event.category
            }
        })
            .then(res => {
                console.log("Edited book")
            })
            .catch(err => console.log(err))
    };

    return(
        <div id='book-list'>
            {books.map((book) => {
                return(
                    <div className='book-item'>
                        <p>Title: {book.title}</p>
                        <p>Author: {book.author}</p>
                        <p>Genre: {book.genre}</p>
                        <form>
                            <select 
                                name="category"
                            >
                                <option value="">Select Category</option>
                                <option value="Planning-to-Read">Planning to Read</option>
                                <option value="Currently-Reading">Currently Reading</option>
                                <option value="Finished-Reading">Finished Reading</option>
                            </select>
                            <button  onClick={editBook}>Edit</button>
                        </form>
                        <button>Delete</button>
                    </div>
                )
            })}
        </div>
    ) 
}

export default BookList;
