import axios from 'axios';
import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/auth.context';


function HomePage(){
    const { user } = useContext(AuthContext);

    const navigate = useNavigate();

    const [state, setState] = useState({
        title: '',
        author: '',
        genre: '',
        category: ''
    });

    const updateState = event => setState({
        ...state,
        [event.target.name]: event.target.value
      });

    const handleSubmit = event => {
        event.preventDefault();
        const storedToken = localStorage.getItem('authToken');
        axios.post(`${process.env.REACT_APP_BACKEND_URL}/api/books`, state, {
            headers: {
            authorization: `Bearer ${storedToken}`
        }
      })
        .then(res => {
          console.log(res.data);
          navigate('/home');
        })
        .catch(err => console.log(err))
        
    };
    return(
        <div>
            <h2>Add Book</h2>
            <form>
                <input  
                type="text"
                placeholder="Title"
                name="title"
                value={state.title}
                onChange={updateState}
                required
                />
                <input
                    type="text"
                    placeholder="Author"
                    name="author"
                    value={state.author}
                    onChange={updateState}
                />
                <input
                    type="text"
                    placeholder="Genre"
                    name="genre"
                    value={state.genre}
                    onChange={updateState}
                />
                <select 
                    name="category"
                    onChange={updateState}
                    required 
                >
                    <option value="">Select Category</option>
                    <option value="Planning-to-Read">Planning to Read</option>
                    <option value="Currently-Reading">Currently Reading</option>
                    <option value="Finished-Reading">Finished Reading</option>
                </select>
                <button onClick={handleSubmit}>Add Book</button>
            </form>
            
            <div>
                <h2>Categories</h2>
                <button>Planning to Read</button>
                <button>Currently Reading</button>
                <button>Finished Reading</button>
            </div>
        </div>
            
    )
}

export default HomePage;
